package org.mindinformatics.grails.domeo.dashboard.security.oauth;import org.scribe.model.Token;import org.scribe.model.Verifier;import org.scribe.oauth.OAuthService;
/** Class to allow the creation of an OAuth2.0Service interface implementing * defaults for the OAuth1 methods and adding methods for OAuth2.0 interaction. * @author Tom Wilkin */
abstract class OAuth20Service implements OAuthService {		@Override	public final String getVersion( ) {		return "2.0";	}		@Override	public final Token getRequestToken( ) {		throw new UnsupportedOperationException("Unsupported operation for OAuth 2, please use 'getAuthorizationUrl' and redirect your users there.");	}		@Override	public final String getAuthorizationUrl(final Token token) {		return getAuthorizationUrl( );	}
	@Override	public final Token getAccessToken(final Token requestToken, final Verifier verifier) {		return getAccessToken(verifier);	}		/** Get the Authorisation URL used to request the verification code.	 * @return The Authorisation URL used to request the verification code. */	public abstract getAuthorizationUrl( );		/** Retrieve the access token from the OAuth service with the previously	 * returned verifier code.	 * @param verifier The verifier code.	 * @return The retrieved access token. */	public abstract getAccessToken(final Verifier verifier);	
};
